"""
 @legend: SMA($ticker)
 @name: Simple Moving Average|SMA
"""
def main(df):
    return df['Close'].rolling(window=5).mean()

XEMA.code:
"""
 @legend: XEMA($ticker)
 @name: Crossing EMA|XEMA
"""

def calculate_ema(df, column, period):
    return df[column].ewm(span=period, adjust=False).mean()

def main(df, short_period=14, long_period=20):
    short_ema = calculate_ema(df, 'Close', short_period)
    long_ema = calculate_ema(df, 'Close', long_period)
    xema = short_ema - long_ema
    return xema

